import React, { useState, useEffect } from 'react';
import {
  Container, Typography, Box, Card, CardContent,
  Alert, Table, TableBody, TableCell, TableContainer,
  TableHead, TableRow, Paper, Chip, Button, Dialog,
  DialogTitle, DialogContent, DialogActions
} from '@mui/material';
import ShoppingBagIcon from '@mui/icons-material/ShoppingBag';
import CancelIcon from '@mui/icons-material/Cancel';

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [orderItems, setOrderItems] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [detailsDialogOpen, setDetailsDialogOpen] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:8080/api/orders');
      if (!response.ok) throw new Error('Ошибка загрузки');
      
      const data = await response.json();
      setOrders(data);
      
      // Загружаем товары для каждого заказа
      for (const order of data) {
        fetchOrderItems(order.id);
      }
    } catch (err) {
      setError('Не удалось загрузить заказы');
    } finally {
      setLoading(false);
    }
  };

  const fetchOrderItems = async (orderId) => {
    try {
      const response = await fetch(`http://localhost:8080/api/orders/${orderId}/items`);
      if (response.ok) {
        const items = await response.json();
        setOrderItems(prev => ({ ...prev, [orderId]: items }));
      }
    } catch (err) {
      console.error('Ошибка загрузки товаров заказа:', err);
    }
  };

  const handleCancelOrder = async (orderId) => {
    if (!window.confirm('Вы уверены, что хотите отменить заказ?')) {
      return;
    }

    try {
      const response = await fetch(`http://localhost:8080/api/orders/${orderId}/cancel`, {
        method: 'POST'
      });
      
      const result = await response.text();
      if (result === 'GOOD') {
        setSuccess('Заказ отменен');
        fetchOrders();
      } else if (result === 'ERRORSTATUS') {
        setError('Нельзя отменить заказ с текущим статусом');
      } else {
        setError('Ошибка отмены заказа');
      }
    } catch (err) {
      setError('Ошибка отмены заказа');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'В обработке': return 'warning';
      case 'Отправлен': return 'info';
      case 'Доставлен': return 'success';
      case 'Отменен': return 'error';
      default: return 'default';
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleString('ru-RU');
  };

  const handleViewDetails = (order) => {
    setSelectedOrder(order);
    setDetailsDialogOpen(true);
  };

  if (loading) {
    return (
      <Container sx={{ textAlign: 'center', mt: 10 }}>
        <Typography variant="h6">Загрузка заказов...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg">
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Мои заказы
        </Typography>
        
        {error && (
          <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
            {error}
          </Alert>
        )}
        
        {success && (
          <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
            {success}
          </Alert>
        )}
      </Box>

      {orders.length === 0 ? (
        <Box sx={{ textAlign: 'center', mt: 10 }}>
          <ShoppingBagIcon sx={{ fontSize: 60, color: 'text.secondary', mb: 2 }} />
          <Typography variant="h6" color="text.secondary" gutterBottom>
            У вас пока нет заказов
          </Typography>
        </Box>
      ) : (
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Номер заказа</TableCell>
                <TableCell>Дата</TableCell>
                <TableCell>Статус</TableCell>
                <TableCell align="right">Сумма</TableCell>
                <TableCell>Адрес доставки</TableCell>
                <TableCell align="center">Действия</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {orders.map((order) => (
                <TableRow key={order.id}>
                  <TableCell>#{order.id}</TableCell>
                  <TableCell>{formatDate(order.orderDate)}</TableCell>
                  <TableCell>
                    <Chip
                      label={order.status}
                      color={getStatusColor(order.status)}
                      size="small"
                    />
                  </TableCell>
                  <TableCell align="right">
                    <Typography variant="h6">
                      {order.totalAmount.toFixed(2)} ₽
                    </Typography>
                  </TableCell>
                  <TableCell>{order.deliveryAddress}</TableCell>
                  <TableCell align="center">
                    <Box sx={{ display: 'flex', gap: 1, justifyContent: 'center' }}>
                      <Button
                        size="small"
                        onClick={() => handleViewDetails(order)}
                      >
                        Детали
                      </Button>
                      {order.status === 'В обработке' && (
                        <Button
                          size="small"
                          color="error"
                          startIcon={<CancelIcon />}
                          onClick={() => handleCancelOrder(order.id)}
                        >
                          Отменить
                        </Button>
                      )}
                    </Box>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}

      {/* Диалог деталей заказа */}
      <Dialog open={detailsDialogOpen} onClose={() => setDetailsDialogOpen(false)} maxWidth="md" fullWidth>
        <DialogTitle>
          Детали заказа #{selectedOrder?.id}
        </DialogTitle>
        <DialogContent>
          {selectedOrder && (
            <Box>
              <Typography variant="body2" color="text.secondary" gutterBottom>
                Дата: {formatDate(selectedOrder.orderDate)}
              </Typography>
              <Typography variant="body2" color="text.secondary" gutterBottom>
                Статус: <Chip label={selectedOrder.status} color={getStatusColor(selectedOrder.status)} size="small" />
              </Typography>
              <Typography variant="body2" color="text.secondary" gutterBottom>
                Адрес доставки: {selectedOrder.deliveryAddress}
              </Typography>
              
              <Typography variant="h6" sx={{ mt: 3, mb: 2 }}>
                Товары:
              </Typography>
              
              {orderItems[selectedOrder.id] ? (
                <TableContainer>
                  <Table size="small">
                    <TableHead>
                      <TableRow>
                        <TableCell>Товар</TableCell>
                        <TableCell align="right">Цена</TableCell>
                        <TableCell align="center">Количество</TableCell>
                        <TableCell align="right">Сумма</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {orderItems[selectedOrder.id].map((item) => (
                        <TableRow key={item.id}>
                          <TableCell>{item.product.name}</TableCell>
                          <TableCell align="right">{item.price.toFixed(2)} ₽</TableCell>
                          <TableCell align="center">{item.quantity}</TableCell>
                          <TableCell align="right">{(item.price * item.quantity).toFixed(2)} ₽</TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              ) : (
                <Typography>Загрузка...</Typography>
              )}
              
              <Box sx={{ mt: 2, textAlign: 'right' }}>
                <Typography variant="h6">
                  Итого: {selectedOrder.totalAmount.toFixed(2)} ₽
                </Typography>
              </Box>
            </Box>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDetailsDialogOpen(false)}>Закрыть</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Orders;
