import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container, Typography, Box, Grid, Card, CardContent,
  CardMedia, Button, Alert
} from '@mui/material';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import CategoryIcon from '@mui/icons-material/Category';
import LocalShippingIcon from '@mui/icons-material/LocalShipping';

const HomePage = () => {
  const [userRole, setUserRole] = useState('NULL');
  const [products, setProducts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    checkUser();
    fetchProducts();
  }, []);

  const checkUser = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/current-user');
      const role = await response.text();
      setUserRole(role);
    } catch (error) {
      console.error('Ошибка:', error);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/products');
      const data = await response.json();
      // Берем только 4 товара для главной страницы
      setProducts(data.slice(0, 4));
    } catch (error) {
      console.error('Ошибка загрузки товаров:', error);
    }
  };

  const features = [
    {
      icon: <ShoppingCartIcon sx={{ fontSize: 60 }} color="primary" />,
      title: 'Удобная корзина',
      description: 'Добавляйте товары в корзину и оформляйте заказы в несколько кликов'
    },
    {
      icon: <CategoryIcon sx={{ fontSize: 60 }} color="secondary" />,
      title: 'Широкий ассортимент',
      description: 'Большой выбор товаров различных категорий с подробными описаниями'
    },
    {
      icon: <LocalShippingIcon sx={{ fontSize: 60 }} color="success" />,
      title: 'Быстрая доставка',
      description: 'Оформляйте заказы и отслеживайте их статус в личном кабинете'
    }
  ];

  return (
    <Container maxWidth="lg">
      <Box sx={{ textAlign: 'center', my: 8 }}>
        <Typography variant="h2" component="h1" gutterBottom sx={{ fontWeight: 'bold' }}>
          Добро пожаловать в интернет-магазин
        </Typography>
        <Typography variant="h5" color="text.secondary" paragraph>
          Широкий ассортимент товаров по выгодным ценам
        </Typography>
        
        {userRole === 'NULL' && (
          <Box sx={{ mt: 4 }}>
            <Alert severity="info" sx={{ mb: 2 }}>
              Для оформления заказов войдите в систему или зарегистрируйтесь
            </Alert>
            <Button
              variant="contained"
              size="large"
              onClick={() => navigate('/login')}
              sx={{ mr: 2 }}
            >
              Войти
            </Button>
            <Button
              variant="outlined"
              size="large"
              onClick={() => navigate('/register')}
            >
              Зарегистрироваться
            </Button>
          </Box>
        )}
      </Box>

      {/* Особенности */}
      <Box sx={{ my: 8 }}>
        <Typography variant="h4" component="h2" gutterBottom align="center">
          Преимущества нашего магазина
        </Typography>
        <Grid container spacing={4} sx={{ mt: 2 }}>
          {features.map((feature, index) => (
            <Grid item xs={12} md={4} key={index}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', p: 3 }}>
                {feature.icon}
                <CardContent sx={{ textAlign: 'center' }}>
                  <Typography variant="h5" component="h3" gutterBottom>
                    {feature.title}
                  </Typography>
                  <Typography color="text.secondary">
                    {feature.description}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>

      {/* Популярные товары */}
      <Box sx={{ my: 8 }}>
        <Typography variant="h4" component="h2" gutterBottom>
          Популярные товары
        </Typography>
        <Grid container spacing={3}>
          {products.map((product) => (
            <Grid item xs={12} sm={6} md={3} key={product.id}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <CardMedia
                  component="img"
                  height="200"
                  image={product.imageUrl || 'https://via.placeholder.com/300x200?text=No+Image'}
                  alt={product.name}
                />
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography gutterBottom variant="h6" component="h3">
                    {product.name}
                  </Typography>
                  <Typography variant="h6" color="primary" paragraph>
                    {product.price.toFixed(2)} ₽
                  </Typography>
                  <Typography variant="body2" color="text.secondary" paragraph>
                    {product.category ? product.category.name : 'Без категории'}
                  </Typography>
                  <Button
                    size="small"
                    onClick={() => navigate('/products')}
                    fullWidth
                    variant="contained"
                  >
                    Подробнее
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Container>
  );
};

export default HomePage;
