import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container, Grid, Card, CardContent, CardMedia,
  Typography, Button, Rating, TextField, Box,
  FormControl, InputLabel, Input, Alert, Chip,
  IconButton, Dialog, DialogTitle, DialogContent,
  DialogActions
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import MovieIcon from '@mui/icons-material/Movie';

const AllFilms = () => {
  const [films, setFilms] = useState([]);
  const [filteredFilms, setFilteredFilms] = useState([]);
  const [userRole, setUserRole] = useState('NULL');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [selectedFilm, setSelectedFilm] = useState(null);
  const [editForm, setEditForm] = useState({
    headname: '',
    director: '',
    zhanr: '',
    aboutis: '',
    year: '',
    urlimage: ''
  });
  const navigate = useNavigate();

  useEffect(() => {
    checkUser();
    fetchFilms();
  }, []);

  useEffect(() => {
    if (searchTerm.trim() === '') {
      setFilteredFilms(films);
    } else {
      const filtered = films.filter(film =>
        film.headname.toLowerCase().includes(searchTerm.toLowerCase()) ||
        film.director.toLowerCase().includes(searchTerm.toLowerCase()) ||
        film.zhanr.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredFilms(filtered);
    }
  }, [searchTerm, films]);

  const checkUser = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/current-user');
      const role = await response.text();
      setUserRole(role);
    } catch (error) {
      console.error('Ошибка при проверке пользователя:', error);
    }
  };

  const fetchFilms = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:8080/api/films');
      if (!response.ok) throw new Error('Ошибка загрузки фильмов');
      
      const data = await response.json();
      setFilms(data);
      setFilteredFilms(data);
    } catch (err) {
      setError('Не удалось загрузить фильмы');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    if (searchTerm.trim() === '') {
      fetchFilms();
      return;
    }
    
    try {
      const response = await fetch('http://localhost:8080/api/films/search', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ headname: searchTerm })
      });
      
      const data = await response.json();
      setFilteredFilms(data);
    } catch (err) {
      setError('Ошибка поиска');
    }
  };

  const handleAddToMyCollection = async (film) => {
    if (userRole === 'NULL') {
      navigate('/login');
      return;
    }
    
    try {
      const response = await fetch('http://localhost:8080/api/my-films/add', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(film)
      });
      
      const result = await response.text();
      if (result === 'GOOD') {
        setSuccess(`Фильм "${film.headname}" добавлен в вашу коллекцию`);
      } else {
        setError('Этот фильм уже есть в вашей коллекции');
      }
    } catch (err) {
      setError('Ошибка добавления фильма');
    }
  };

  const handleEditClick = (film) => {
    setSelectedFilm(film);
    setEditForm({
      headname: film.headname,
      director: film.director,
      zhanr: film.zhanr,
      aboutis: film.aboutis,
      year: film.year,
      urlimage: film.urlimage
    });
    setEditDialogOpen(true);
  };

  const handleEditSubmit = async () => {
    try {
      const filmData = {
        id: selectedFilm.id,
        ...editForm,
        year: parseInt(editForm.year)
      };
      
      const response = await fetch('http://localhost:8080/api/films/edit', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(filmData)
      });
      
      const result = await response.text();
      if (result.startsWith('1.')) {
        setSuccess(result);
        setEditDialogOpen(false);
        fetchFilms();
      } else {
        setError(result);
      }
    } catch (err) {
      setError('Ошибка редактирования фильма');
    }
  };

  if (loading) {
    return (
      <Container sx={{ textAlign: 'center', mt: 10 }}>
        <Typography variant="h6">Загрузка фильмов...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="xl">
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Все фильмы
        </Typography>
        
        {error && (
          <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
            {error}
          </Alert>
        )}
        
        {success && (
          <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
            {success}
          </Alert>
        )}
        
        {/* Поиск */}
        <Paper elevation={1} sx={{ p: 2, mb: 3 }}>
          <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Поиск по названию, режиссеру или жанру..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
              InputProps={{
                startAdornment: <SearchIcon sx={{ mr: 1, color: 'action.active' }} />
              }}
            />
            <Button
              variant="contained"
              onClick={handleSearch}
              startIcon={<SearchIcon />}
            >
              Поиск
            </Button>
          </Box>
        </Paper>
      </Box>

      {/* Сетка фильмов */}
      <Grid container spacing={3}>
        {filteredFilms.map((film) => (
          <Grid item xs={12} sm={6} md={4} lg={3} key={film.id}>
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
              <CardMedia
                component="img"
                height="300"
                image={film.urlimage || 'https://via.placeholder.com/300x200?text=No+Image'}
                alt={film.headname}
                sx={{ objectFit: 'cover' }}
              />
              <CardContent sx={{ flexGrow: 1 }}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start', mb: 1 }}>
                  <Typography variant="h6" component="h3">
                    {film.headname}
                  </Typography>
                  {userRole === 'ADMIN' && (
                    <IconButton
                      size="small"
                      onClick={() => handleEditClick(film)}
                      color="primary"
                    >
                      <EditIcon />
                    </IconButton>
                  )}
                </Box>
                
                <Typography variant="body2" color="text.secondary" paragraph>
                  {film.director} • {film.year}
                </Typography>
                
                <Chip
                  label={film.zhanr}
                  size="small"
                  sx={{ mb: 1 }}
                />
                
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <Rating value={film.rating} precision={0.5} readOnly size="small" />
                  <Typography variant="body2" color="text.secondary" sx={{ ml: 1 }}>
                    {film.rating.toFixed(1)} ({film.marks} оценок)
                  </Typography>
                </Box>
                
                <Typography variant="body2" color="text.secondary" paragraph sx={{ 
                  display: '-webkit-box',
                  WebkitLineClamp: 3,
                  WebkitBoxOrient: 'vertical',
                  overflow: 'hidden'
                }}>
                  {film.aboutis}
                </Typography>
                
                <Box sx={{ mt: 'auto' }}>
                  {userRole !== 'NULL' ? (
                    <Button
                      variant="contained"
                      fullWidth
                      startIcon={<AddIcon />}
                      onClick={() => handleAddToMyCollection(film)}
                    >
                      В мою коллекцию
                    </Button>
                  ) : (
                    <Button
                      variant="outlined"
                      fullWidth
                      onClick={() => navigate('/login')}
                    >
                      Войдите, чтобы добавить
                    </Button>
                  )}
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {filteredFilms.length === 0 && (
        <Box sx={{ textAlign: 'center', mt: 10 }}>
          <MovieIcon sx={{ fontSize: 60, color: 'text.secondary', mb: 2 }} />
          <Typography variant="h6" color="text.secondary">
            Фильмы не найдены
          </Typography>
        </Box>
      )}

      {/* Диалог редактирования */}
      <Dialog open={editDialogOpen} onClose={() => setEditDialogOpen(false)} maxWidth="md" fullWidth>
        <DialogTitle>Редактирование фильма</DialogTitle>
        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Название фильма"
                value={editForm.headname}
                onChange={(e) => setEditForm({ ...editForm, headname: e.target.value })}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Режиссер"
                value={editForm.director}
                onChange={(e) => setEditForm({ ...editForm, director: e.target.value })}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Год"
                type="number"
                value={editForm.year}
                onChange={(e) => setEditForm({ ...editForm, year: e.target.value })}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Жанр"
                value={editForm.zhanr}
                onChange={(e) => setEditForm({ ...editForm, zhanr: e.target.value })}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="URL постера"
                value={editForm.urlimage}
                onChange={(e) => setEditForm({ ...editForm, urlimage: e.target.value })}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Описание"
                multiline
                rows={4}
                value={editForm.aboutis}
                onChange={(e) => setEditForm({ ...editForm, aboutis: e.target.value })}
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setEditDialogOpen(false)}>Отмена</Button>
          <Button onClick={handleEditSubmit} variant="contained">Сохранить</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default AllFilms;
