import React, { useState, useEffect } from 'react';
import {
  Container, Grid, Card, CardContent, CardActions,
  Typography, Button, TextField, Box, Alert,
  Avatar, Chip, Paper
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import PersonAddIcon from '@mui/icons-material/PersonAdd';
import PersonIcon from '@mui/icons-material/Person';
import PeopleIcon from '@mui/icons-material/People';

const FindFriends = () => {
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [addedUsers, setAddedUsers] = useState(new Set());

  useEffect(() => {
    fetchUsers();
  }, []);

  useEffect(() => {
    if (searchTerm.trim() === '') {
      setFilteredUsers(users);
    } else {
      const filtered = users.filter(user =>
        user.login.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredUsers(filtered);
    }
  }, [searchTerm, users]);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:8080/api/users/find-friends');
      if (!response.ok) throw new Error('Ошибка загрузки');
      
      const data = await response.json();
      setUsers(data);
      setFilteredUsers(data);
    } catch (err) {
      setError('Не удалось загрузить список пользователей');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    if (searchTerm.trim() === '') {
      fetchUsers();
      return;
    }
    
    try {
      const response = await fetch('http://localhost:8080/api/users/find-friends/search', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ login: searchTerm })
      });
      
      const data = await response.json();
      setFilteredUsers(data);
    } catch (err) {
      setError('Ошибка поиска');
    }
  };

  const handleAddFriend = async (user) => {
    try {
      const response = await fetch('http://localhost:8080/api/friends/add', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(user)
      });
      
      const result = await response.text();
      if (result === 'GOOD') {
        setSuccess(`Пользователь ${user.login} добавлен в друзья`);
        setAddedUsers(new Set([...addedUsers, user.id]));
      } else if (result === 'ERROR1') {
        setError('Этот пользователь уже у вас в друзьях');
      } else if (result === 'ERROR') {
        setError('Нельзя добавить самого себя в друзья');
      } else {
        setError('Ошибка добавления в друзья');
      }
    } catch (err) {
      setError('Ошибка добавления в друзья');
    }
  };

  if (loading) {
    return (
      <Container sx={{ textAlign: 'center', mt: 10 }}>
        <Typography variant="h6">Загрузка пользователей...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="xl">
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Найти друзей
        </Typography>
        
        {error && (
          <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
            {error}
          </Alert>
        )}
        
        {success && (
          <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
            {success}
          </Alert>
        )}
        
        <Paper elevation={1} sx={{ p: 2, mb: 3 }}>
          <Typography variant="body1" paragraph>
            Здесь отображаются пользователи, которые разрешили другим добавлять себя в друзья.
            Вы можете добавить их в свои друзья, чтобы просматривать их коллекции фильмов.
          </Typography>
        </Paper>
        
        {/* Поиск */}
        <Paper elevation={1} sx={{ p: 2, mb: 3 }}>
          <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Поиск пользователей по логину..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
              InputProps={{
                startAdornment: <SearchIcon sx={{ mr: 1, color: 'action.active' }} />
              }}
            />
            <Button
              variant="contained"
              onClick={handleSearch}
              startIcon={<SearchIcon />}
            >
              Поиск
            </Button>
          </Box>
        </Paper>
      </Box>

      {/* Сетка пользователей */}
      <Grid container spacing={3}>
        {filteredUsers.map((user) => (
          <Grid item xs={12} sm={6} md={4} lg={3} key={user.id}>
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
              <CardContent sx={{ flexGrow: 1, textAlign: 'center' }}>
                <Avatar
                  sx={{
                    width: 80,
                    height: 80,
                    margin: 'auto',
                    mb: 2,
                    bgcolor: addedUsers.has(user.id) ? 'success.main' : 'primary.main'
                  }}
                >
                  {addedUsers.has(user.id) ? (
                    <PeopleIcon sx={{ fontSize: 40 }} />
                  ) : (
                    <PersonIcon sx={{ fontSize: 40 }} />
                  )}
                </Avatar>
                
                <Typography variant="h6" component="h3" gutterBottom>
                  {user.login}
                </Typography>
                
                <Typography variant="body2" color="text.secondary" paragraph>
                  {user.email}
                </Typography>
                
                <Box sx={{ display: 'flex', gap: 1, justifyContent: 'center', mb: 2 }}>
                  <Chip
                    label={user.showotherfilms ? 'Коллекция открыта' : 'Коллекция закрыта'}
                    size="small"
                    color={user.showotherfilms ? 'primary' : 'default'}
                  />
                </Box>
                
                {addedUsers.has(user.id) && (
                  <Chip
                    label="Уже в друзьях"
                    color="success"
                    size="small"
                    sx={{ mb: 2 }}
                  />
                )}
              </CardContent>
              
              <CardActions sx={{ justifyContent: 'center', pb: 2 }}>
                {!addedUsers.has(user.id) ? (
                  <Button
                    variant="contained"
                    startIcon={<PersonAddIcon />}
                    onClick={() => handleAddFriend(user)}
                  >
                    Добавить в друзья
                  </Button>
                ) : (
                  <Button
                    variant="outlined"
                    disabled
                  >
                    Уже в друзьях
                  </Button>
                )}
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      {filteredUsers.length === 0 && (
        <Box sx={{ textAlign: 'center', mt: 10 }}>
          <PeopleIcon sx={{ fontSize: 60, color: 'text.secondary', mb: 2 }} />
          <Typography variant="h6" color="text.secondary">
            {users.length === 0 
              ? 'Нет пользователей, разрешивших добавление в друзья'
              : 'Пользователи не найдены'}
          </Typography>
        </Box>
      )}
    </Container>
  );
};

export default FindFriends;
