import React, { useState, useEffect } from 'react';
import {
  Container, Grid, Card, CardContent, CardActions,
  Typography, Button, TextField, Box, Alert,
  Avatar, Chip, IconButton, Dialog, DialogTitle,
  DialogContent, DialogActions, Paper
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import PersonIcon from '@mui/icons-material/Person';
import DeleteIcon from '@mui/icons-material/Delete';
import VisibilityIcon from '@mui/icons-material/Visibility';
import PeopleIcon from '@mui/icons-material/People';
import MovieIcon from '@mui/icons-material/Movie';

const Friends = () => {
  const [friends, setFriends] = useState([]);
  const [filteredFriends, setFilteredFriends] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [collectionDialogOpen, setCollectionDialogOpen] = useState(false);
  const [selectedFriend, setSelectedFriend] = useState(null);
  const [friendCollection, setFriendCollection] = useState([]);
  const [collectionSearch, setCollectionSearch] = useState('');

  useEffect(() => {
    fetchFriends();
  }, []);

  useEffect(() => {
    if (searchTerm.trim() === '') {
      setFilteredFriends(friends);
    } else {
      const filtered = friends.filter(item =>
        item.usertwo.login.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredFriends(filtered);
    }
  }, [searchTerm, friends]);

  const fetchFriends = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:8080/api/friends');
      if (!response.ok) throw new Error('Ошибка загрузки');
      
      const data = await response.json();
      setFriends(data);
      setFilteredFriends(data);
    } catch (err) {
      setError('Не удалось загрузить список друзей');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    if (searchTerm.trim() === '') {
      fetchFriends();
      return;
    }
    
    try {
      const response = await fetch('http://localhost:8080/api/friends/search', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ login: searchTerm })
      });
      
      const data = await response.json();
      setFilteredFriends(data);
    } catch (err) {
      setError('Ошибка поиска');
    }
  };

  const handleDeleteFriend = async (friend) => {
    if (!window.confirm(`Удалить ${friend.usertwo.login} из друзей?`)) {
      return;
    }
    
    try {
      const response = await fetch('http://localhost:8080/api/friends/delete', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(friend.usertwo)
      });
      
      const result = await response.text();
      if (result === 'GOOD') {
        setSuccess(`Пользователь ${friend.usertwo.login} удален из друзей`);
        fetchFriends();
      } else {
        setError('Ошибка удаления друга');
      }
    } catch (err) {
      setError('Ошибка удаления друга');
    }
  };

  const handleViewCollection = async (friend) => {
    setSelectedFriend(friend);
    try {
      const response = await fetch('http://localhost:8080/api/friends/collection', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(friend.usertwo)
      });
      
      const data = await response.json();
      setFriendCollection(data || []);
      setCollectionDialogOpen(true);
    } catch (err) {
      setError('Не удалось загрузить коллекцию друга');
    }
  };

  const handleCollectionSearch = async () => {
    if (!selectedFriend || collectionSearch.trim() === '') {
      handleViewCollection(selectedFriend);
      return;
    }
    
    try {
      const requestData = {
        userId: selectedFriend.usertwo.id,
        searchText: collectionSearch
      };
      
      const response = await fetch('http://localhost:8080/api/friends/collection/search', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestData)
      });
      
      const data = await response.json();
      setFriendCollection(data || []);
    } catch (err) {
      setError('Ошибка поиска в коллекции');
    }
  };

  if (loading) {
    return (
      <Container sx={{ textAlign: 'center', mt: 10 }}>
        <Typography variant="h6">Загрузка списка друзей...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="xl">
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Мои друзья
        </Typography>
        
        {error && (
          <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
            {error}
          </Alert>
        )}
        
        {success && (
          <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
            {success}
          </Alert>
        )}
        
        {/* Статистика */}
        <Paper elevation={1} sx={{ p: 2, mb: 3 }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <PeopleIcon color="primary" />
            <Typography variant="h6">
              Всего друзей: {friends.length}
            </Typography>
          </Box>
        </Paper>
        
        {/* Поиск */}
        <Paper elevation={1} sx={{ p: 2, mb: 3 }}>
          <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Поиск друзей по логину..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
              InputProps={{
                startAdornment: <SearchIcon sx={{ mr: 1, color: 'action.active' }} />
              }}
            />
            <Button
              variant="contained"
              onClick={handleSearch}
              startIcon={<SearchIcon />}
            >
              Поиск
            </Button>
          </Box>
        </Paper>
      </Box>

      {/* Сетка друзей */}
      <Grid container spacing={3}>
        {filteredFriends.map((friend) => (
          <Grid item xs={12} sm={6} md={4} lg={3} key={friend.id}>
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
              <CardContent sx={{ flexGrow: 1, textAlign: 'center' }}>
                <Avatar
                  sx={{
                    width: 80,
                    height: 80,
                    margin: 'auto',
                    mb: 2,
                    bgcolor: 'primary.main'
                  }}
                >
                  <PersonIcon sx={{ fontSize: 40 }} />
                </Avatar>
                
                <Typography variant="h6" component="h3" gutterBottom>
                  {friend.usertwo.login}
                </Typography>
                
                <Typography variant="body2" color="text.secondary" paragraph>
                  {friend.usertwo.email}
                </Typography>
                
                <Box sx={{ display: 'flex', gap: 1, justifyContent: 'center', mb: 2 }}>
                  <Chip
                    label={friend.usertwo.showforaddfr ? 'Виден для добавления' : 'Скрыт'}
                    size="small"
                    color={friend.usertwo.showforaddfr ? 'success' : 'default'}
                  />
                  <Chip
                    label={friend.usertwo.showotherfilms ? 'Коллекция открыта' : 'Коллекция закрыта'}
                    size="small"
                    color={friend.usertwo.showotherfilms ? 'primary' : 'default'}
                  />
                </Box>
              </CardContent>
              
              <CardActions sx={{ justifyContent: 'center', pb: 2 }}>
                {friend.usertwo.showotherfilms && (
                  <Button
                    variant="contained"
                    startIcon={<VisibilityIcon />}
                    onClick={() => handleViewCollection(friend)}
                  >
                    Коллекция
                  </Button>
                )}
                <IconButton
                  color="error"
                  onClick={() => handleDeleteFriend(friend)}
                >
                  <DeleteIcon />
                </IconButton>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      {filteredFriends.length === 0 && (
        <Box sx={{ textAlign: 'center', mt: 10 }}>
          <PeopleIcon sx={{ fontSize: 60, color: 'text.secondary', mb: 2 }} />
          <Typography variant="h6" color="text.secondary">
            {friends.length === 0 
              ? 'У вас пока нет друзей. Найдите друзей в разделе "Найти друзей"!'
              : 'Друзья не найдены'}
          </Typography>
        </Box>
      )}

      {/* Диалог коллекции друга */}
      <Dialog
        open={collectionDialogOpen}
        onClose={() => setCollectionDialogOpen(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>
          Коллекция фильмов {selectedFriend?.usertwo.login}
        </DialogTitle>
        <DialogContent>
          {selectedFriend && (
            <>
              {/* Поиск в коллекции */}
              <Box sx={{ display: 'flex', gap: 2, mb: 3, mt: 2 }}>
                <TextField
                  fullWidth
                  variant="outlined"
                  placeholder="Поиск в коллекции..."
                  value={collectionSearch}
                  onChange={(e) => setCollectionSearch(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleCollectionSearch()}
                  InputProps={{
                    startAdornment: <SearchIcon sx={{ mr: 1, color: 'action.active' }} />
                  }}
                />
                <Button
                  variant="contained"
                  onClick={handleCollectionSearch}
                  startIcon={<SearchIcon />}
                >
                  Поиск
                </Button>
              </Box>
              
              {/* Фильмы в коллекции */}
              <Grid container spacing={2}>
                {friendCollection.map((item) => (
                  <Grid item xs={12} sm={6} key={item.id}>
                    <Card>
                      <CardContent>
                        <Box sx={{ display: 'flex', alignItems: 'start', gap: 2 }}>
                          {item.cinema.urlimage && (
                            <img
                              src={item.cinema.urlimage}
                              alt={item.cinema.headname}
                              style={{ width: 80, height: 120, objectFit: 'cover' }}
                            />
                          )}
                          <Box sx={{ flexGrow: 1 }}>
                            <Typography variant="h6">
                              {item.cinema.headname}
                            </Typography>
                            <Typography variant="body2" color="text.secondary">
                              {item.cinema.director} • {item.cinema.year}
                            </Typography>
                            <Chip
                              label={item.cinema.zhanr}
                              size="small"
                              sx={{ mt: 1, mr: 1 }}
                            />
                            <Chip
                              label={item.statuscinema}
                              size="small"
                              color="primary"
                              sx={{ mt: 1 }}
                            />
                            {item.ratinguser > 0 && (
                              <Box sx={{ display: 'flex', alignItems: 'center', mt: 1 }}>
                                <MovieIcon sx={{ mr: 1, fontSize: 20 }} color="action" />
                                <Typography variant="body2">
                                  Оценка: {item.ratinguser}/10
                                </Typography>
                              </Box>
                            )}
                          </Box>
                        </Box>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
              
              {friendCollection.length === 0 && (
                <Box sx={{ textAlign: 'center', py: 4 }}>
                  <MovieIcon sx={{ fontSize: 60, color: 'text.secondary', mb: 2 }} />
                  <Typography variant="h6" color="text.secondary">
                    Коллекция пуста или скрыта
                  </Typography>
                </Box>
              )}
            </>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCollectionDialogOpen(false)}>Закрыть</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Friends;
