import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container, Box, TextField, Button, Typography,
  Alert, Paper, Avatar, Grid
} from '@mui/material';
import AddBoxIcon from '@mui/icons-material/AddBox';
import MovieIcon from '@mui/icons-material/Movie';

const AddFilm = () => {
  const [formData, setFormData] = useState({
    headname: '',
    urlimage: '',
    director: '',
    zhanr: '',
    aboutis: '',
    year: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
    setSuccess('');
  };

  const validateForm = () => {
    const required = ['headname', 'director', 'zhanr', 'year'];
    for (const field of required) {
      if (!formData[field].trim()) {
        setError(`Поле "${getFieldLabel(field)}" обязательно для заполнения`);
        return false;
      }
    }
    
    const year = parseInt(formData.year);
    if (isNaN(year) || year < 1900 || year > new Date().getFullYear() + 5) {
      setError('Введите корректный год');
      return false;
    }
    
    return true;
  };

  const getFieldLabel = (field) => {
    const labels = {
      headname: 'Название фильма',
      urlimage: 'URL постера',
      director: 'Режиссер',
      zhanr: 'Жанр',
      aboutis: 'Описание',
      year: 'Год'
    };
    return labels[field] || field;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    if (!validateForm()) {
      return;
    }
    
    setLoading(true);
    
    try {
      const filmData = {
        ...formData,
        year: parseInt(formData.year),
        marks: 0,
        rating: 0
      };
      
      const response = await fetch('http://localhost:8080/api/films/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(filmData),
      });

      const result = await response.text();
      
      if (result.startsWith('1.')) {
        setSuccess(result);
        setTimeout(() => navigate('/films'), 1500);
      } else {
        setError(result);
      }
    } catch (err) {
      setError('Ошибка подключения к серверу');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container component="main" maxWidth="md">
      <Box
        sx={{
          marginTop: 4,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
        }}
      >
        <Paper elevation={3} sx={{ p: 4, width: '100%' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
            <Avatar sx={{ mr: 2, bgcolor: 'primary.main' }}>
              <AddBoxIcon />
            </Avatar>
            <Typography component="h1" variant="h5">
              Добавление нового фильма
            </Typography>
          </Box>
          
          {error && (
            <Alert severity="error" sx={{ mt: 2, mb: 2 }}>
              {error}
            </Alert>
          )}
          
          {success && (
            <Alert severity="success" sx={{ mt: 2, mb: 2 }}>
              {success}
            </Alert>
          )}
          
          <Box component="form" onSubmit={handleSubmit}>
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="headname"
                  label="Название фильма"
                  name="headname"
                  autoComplete="off"
                  autoFocus
                  value={formData.headname}
                  onChange={handleChange}
                  disabled={loading}
                />
              </Grid>
              
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  id="urlimage"
                  label="URL постера фильма"
                  name="urlimage"
                  autoComplete="off"
                  value={formData.urlimage}
                  onChange={handleChange}
                  disabled={loading}
                  placeholder="https://example.com/poster.jpg"
                  helperText="Оставьте пустым, если нет постера"
                />
              </Grid>
              
              <Grid item xs={12} md={6}>
                <TextField
                  required
                  fullWidth
                  id="director"
                  label="Режиссер"
                  name="director"
                  autoComplete="off"
                  value={formData.director}
                  onChange={handleChange}
                  disabled={loading}
                />
              </Grid>
              
              <Grid item xs={12} md={6}>
                <TextField
                  required
                  fullWidth
                  id="zhanr"
                  label="Жанр"
                  name="zhanr"
                  autoComplete="off"
                  value={formData.zhanr}
                  onChange={handleChange}
                  disabled={loading}
                  placeholder="Драма, Комедия, Боевик и т.д."
                />
              </Grid>
              
              <Grid item xs={12} md={6}>
                <TextField
                  required
                  fullWidth
                  id="year"
                  label="Год выпуска"
                  name="year"
                  type="number"
                  autoComplete="off"
                  value={formData.year}
                  onChange={handleChange}
                  disabled={loading}
                  inputProps={{ min: "1900", max: new Date().getFullYear() + 5 }}
                />
              </Grid>
              
              <Grid item xs={12}>
                <TextField
                  fullWidth
                  id="aboutis"
                  label="Описание фильма"
                  name="aboutis"
                  autoComplete="off"
                  multiline
                  rows={4}
                  value={formData.aboutis}
                  onChange={handleChange}
                  disabled={loading}
                  placeholder="Краткое описание сюжета..."
                />
              </Grid>
            </Grid>
            
            <Box sx={{ mt: 4, display: 'flex', justifyContent: 'space-between' }}>
              <Button
                variant="outlined"
                onClick={() => navigate('/films')}
                disabled={loading}
              >
                Отмена
              </Button>
              <Button
                type="submit"
                variant="contained"
                disabled={loading}
                startIcon={<MovieIcon />}
              >
                {loading ? 'Добавление...' : 'Добавить фильм'}
              </Button>
            </Box>
          </Box>
        </Paper>
      </Box>
    </Container>
  );
};

export default AddFilm;
