import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container, Grid, Card, CardContent, CardMedia,
  Typography, Button, Box, Alert, IconButton,
  TextField, Dialog, DialogTitle, DialogContent,
  DialogActions, Paper, Table, TableBody, TableCell,
  TableContainer, TableHead, TableRow
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import RemoveIcon from '@mui/icons-material/Remove';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [orderDialogOpen, setOrderDialogOpen] = useState(false);
  const [deliveryAddress, setDeliveryAddress] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchCart();
  }, []);

  const fetchCart = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:8080/api/cart');
      if (!response.ok) throw new Error('Ошибка загрузки');
      
      const data = await response.json();
      setCartItems(data);
    } catch (err) {
      setError('Не удалось загрузить корзину');
    } finally {
      setLoading(false);
    }
  };

  const updateQuantity = async (item, newQuantity) => {
    if (newQuantity < 1) return;
    if (newQuantity > item.product.stockQuantity) {
      setError('Недостаточно товара на складе');
      return;
    }

    try {
      const response = await fetch('http://localhost:8080/api/cart/update', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...item, quantity: newQuantity })
      });
      
      const result = await response.text();
      if (result === 'GOOD') {
        fetchCart();
      } else if (result === 'ERRORSTOCK') {
        setError('Недостаточно товара на складе');
      } else {
        setError('Ошибка обновления');
      }
    } catch (err) {
      setError('Ошибка обновления количества');
    }
  };

  const deleteItem = async (item) => {
    try {
      const response = await fetch('http://localhost:8080/api/cart/delete', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ id: item.id })
      });
      
      const result = await response.text();
      if (result === 'GOOD') {
        setSuccess('Товар удален из корзины');
        fetchCart();
      } else {
        setError('Ошибка удаления');
      }
    } catch (err) {
      setError('Ошибка удаления товара');
    }
  };

  const calculateTotal = () => {
    return cartItems.reduce((sum, item) => sum + item.product.price * item.quantity, 0);
  };

  const handleOrder = async () => {
    if (!deliveryAddress.trim()) {
      setError('Укажите адрес доставки');
      return;
    }

    try {
      const response = await fetch('http://localhost:8080/api/orders/create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ deliveryAddress })
      });
      
      const result = await response.text();
      if (result === 'GOOD') {
        setSuccess('Заказ успешно оформлен!');
        setOrderDialogOpen(false);
        setDeliveryAddress('');
        fetchCart();
        setTimeout(() => navigate('/orders'), 2000);
      } else if (result.startsWith('ERRORSTOCK_')) {
        setError('Недостаточно товара на складе: ' + result.replace('ERRORSTOCK_', ''));
      } else if (result === 'ERROREMPTY') {
        setError('Корзина пуста');
      } else {
        setError('Ошибка оформления заказа');
      }
    } catch (err) {
      setError('Ошибка оформления заказа');
    }
  };

  if (loading) {
    return (
      <Container sx={{ textAlign: 'center', mt: 10 }}>
        <Typography variant="h6">Загрузка корзины...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg">
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Корзина
        </Typography>
        
        {error && (
          <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
            {error}
          </Alert>
        )}
        
        {success && (
          <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
            {success}
          </Alert>
        )}
      </Box>

      {cartItems.length === 0 ? (
        <Box sx={{ textAlign: 'center', mt: 10 }}>
          <ShoppingCartIcon sx={{ fontSize: 60, color: 'text.secondary', mb: 2 }} />
          <Typography variant="h6" color="text.secondary" gutterBottom>
            Ваша корзина пуста
          </Typography>
          <Button
            variant="contained"
            onClick={() => navigate('/products')}
            sx={{ mt: 2 }}
          >
            Перейти к каталогу
          </Button>
        </Box>
      ) : (
        <>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Товар</TableCell>
                  <TableCell align="right">Цена</TableCell>
                  <TableCell align="center">Количество</TableCell>
                  <TableCell align="right">Сумма</TableCell>
                  <TableCell align="right">Действия</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {cartItems.map((item) => (
                  <TableRow key={item.id}>
                    <TableCell>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                        <CardMedia
                          component="img"
                          sx={{ width: 80, height: 80, objectFit: 'cover' }}
                          image={item.product.imageUrl || 'https://via.placeholder.com/80?text=No+Image'}
                          alt={item.product.name}
                        />
                        <Box>
                          <Typography variant="subtitle1">{item.product.name}</Typography>
                          <Typography variant="body2" color="text.secondary">
                            {item.product.category ? item.product.category.name : 'Без категории'}
                          </Typography>
                        </Box>
                      </Box>
                    </TableCell>
                    <TableCell align="right">
                      <Typography variant="body1">
                        {item.product.price.toFixed(2)} ₽
                      </Typography>
                    </TableCell>
                    <TableCell align="center">
                      <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 1 }}>
                        <IconButton
                          size="small"
                          onClick={() => updateQuantity(item, item.quantity - 1)}
                        >
                          <RemoveIcon />
                        </IconButton>
                        <TextField
                          type="number"
                          value={item.quantity}
                          onChange={(e) => updateQuantity(item, parseInt(e.target.value) || 1)}
                          inputProps={{ min: 1, max: item.product.stockQuantity }}
                          sx={{ width: 60 }}
                          size="small"
                        />
                        <IconButton
                          size="small"
                          onClick={() => updateQuantity(item, item.quantity + 1)}
                          disabled={item.quantity >= item.product.stockQuantity}
                        >
                          <AddIcon />
                        </IconButton>
                      </Box>
                    </TableCell>
                    <TableCell align="right">
                      <Typography variant="h6">
                        {(item.product.price * item.quantity).toFixed(2)} ₽
                      </Typography>
                    </TableCell>
                    <TableCell align="right">
                      <IconButton
                        color="error"
                        onClick={() => deleteItem(item)}
                      >
                        <DeleteIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>

          <Paper elevation={2} sx={{ p: 3, mt: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Typography variant="h5">
                Итого: {calculateTotal().toFixed(2)} ₽
              </Typography>
              <Button
                variant="contained"
                size="large"
                onClick={() => setOrderDialogOpen(true)}
              >
                Оформить заказ
              </Button>
            </Box>
          </Paper>
        </>
      )}

      {/* Диалог оформления заказа */}
      <Dialog open={orderDialogOpen} onClose={() => setOrderDialogOpen(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Оформление заказа</DialogTitle>
        <DialogContent>
          <Box sx={{ mt: 2 }}>
            <Typography variant="h6" gutterBottom>
              Итого к оплате: {calculateTotal().toFixed(2)} ₽
            </Typography>
            <TextField
              fullWidth
              label="Адрес доставки"
              multiline
              rows={3}
              value={deliveryAddress}
              onChange={(e) => setDeliveryAddress(e.target.value)}
              sx={{ mt: 2 }}
              required
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOrderDialogOpen(false)}>Отмена</Button>
          <Button onClick={handleOrder} variant="contained" disabled={!deliveryAddress.trim()}>
            Оформить заказ
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Cart;
