import React, { useState, useEffect } from 'react';
import {
  Container, Grid, Card, CardContent, CardMedia,
  Typography, Button, Rating, TextField, Box,
  Alert, Chip, IconButton, Select, MenuItem,
  FormControl, InputLabel, Dialog, DialogTitle,
  DialogContent, DialogActions, Paper
} from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import MovieIcon from '@mui/icons-material/Movie';

const MyFilms = () => {
  const [myFilms, setMyFilms] = useState([]);
  const [filteredFilms, setFilteredFilms] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [selectedFilm, setSelectedFilm] = useState(null);
  const [editForm, setEditForm] = useState({
    statuscinema: '',
    ratinguser: 0
  });

  const statusOptions = [
    'Без статуса просмотра',
    'Хочу посмотреть',
    'Смотрю сейчас',
    'Просмотрено',
    'Отложено',
    'Брошено'
  ];

  useEffect(() => {
    fetchMyFilms();
  }, []);

  useEffect(() => {
    if (searchTerm.trim() === '') {
      setFilteredFilms(myFilms);
    } else {
      const filtered = myFilms.filter(item =>
        item.cinema.headname.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredFilms(filtered);
    }
  }, [searchTerm, myFilms]);

  const fetchMyFilms = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:8080/api/my-films');
      if (!response.ok) throw new Error('Ошибка загрузки');
      
      const data = await response.json();
      setMyFilms(data);
      setFilteredFilms(data);
    } catch (err) {
      setError('Не удалось загрузить вашу коллекцию');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    if (searchTerm.trim() === '') {
      fetchMyFilms();
      return;
    }
    
    try {
      const response = await fetch('http://localhost:8080/api/my-films/search', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ headname: searchTerm })
      });
      
      const data = await response.json();
      setFilteredFilms(data);
    } catch (err) {
      setError('Ошибка поиска');
    }
  };

  const handleEditClick = (filmUser) => {
    setSelectedFilm(filmUser);
    setEditForm({
      statuscinema: filmUser.statuscinema,
      ratinguser: filmUser.ratinguser
    });
    setEditDialogOpen(true);
  };

  const handleEditSubmit = async () => {
    try {
      const updateData = {
        id: selectedFilm.id,
        ...editForm
      };
      
      const response = await fetch('http://localhost:8080/api/my-films/update', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updateData)
      });
      
      const result = await response.text();
      if (result.startsWith('1.')) {
        setSuccess(result);
        setEditDialogOpen(false);
        fetchMyFilms();
      } else {
        setError(result);
      }
    } catch (err) {
      setError('Ошибка сохранения изменений');
    }
  };

  const handleDelete = async (filmUser) => {
    if (!window.confirm(`Удалить "${filmUser.cinema.headname}" из вашей коллекции?`)) {
      return;
    }
    
    try {
      const response = await fetch('http://localhost:8080/api/my-films/delete', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(filmUser.cinema)
      });
      
      const result = await response.text();
      if (result.startsWith('1.')) {
        setSuccess(result);
        fetchMyFilms();
      } else {
        setError(result);
      }
    } catch (err) {
      setError('Ошибка удаления фильма');
    }
  };

  if (loading) {
    return (
      <Container sx={{ textAlign: 'center', mt: 10 }}>
        <Typography variant="h6">Загрузка коллекции...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="xl">
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Моя коллекция фильмов
        </Typography>
        
        {error && (
          <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError('')}>
            {error}
          </Alert>
        )}
        
        {success && (
          <Alert severity="success" sx={{ mb: 2 }} onClose={() => setSuccess('')}>
            {success}
          </Alert>
        )}
        
        {/* Статистика */}
        <Paper elevation={1} sx={{ p: 2, mb: 3 }}>
          <Grid container spacing={2}>
            <Grid item xs={12} md={3}>
              <Typography variant="body2" color="text.secondary">Всего фильмов</Typography>
              <Typography variant="h4">{myFilms.length}</Typography>
            </Grid>
            <Grid item xs={12} md={3}>
              <Typography variant="body2" color="text.secondary">Просмотрено</Typography>
              <Typography variant="h4">
                {myFilms.filter(f => f.statuscinema === 'Просмотрено').length}
              </Typography>
            </Grid>
            <Grid item xs={12} md={3}>
              <Typography variant="body2" color="text.secondary">Средняя оценка</Typography>
              <Typography variant="h4">
                {myFilms.filter(f => f.ratinguser > 0).length > 0
                  ? (myFilms.reduce((sum, f) => sum + f.ratinguser, 0) / 
                     myFilms.filter(f => f.ratinguser > 0).length).toFixed(1)
                  : '0.0'}
              </Typography>
            </Grid>
            <Grid item xs={12} md={3}>
              <Typography variant="body2" color="text.secondary">Оценено</Typography>
              <Typography variant="h4">
                {myFilms.filter(f => f.ratinguser > 0).length}
              </Typography>
            </Grid>
          </Grid>
        </Paper>
        
        {/* Поиск */}
        <Paper elevation={1} sx={{ p: 2, mb: 3 }}>
          <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
            <TextField
              fullWidth
              variant="outlined"
              placeholder="Поиск в вашей коллекции..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
              InputProps={{
                startAdornment: <SearchIcon sx={{ mr: 1, color: 'action.active' }} />
              }}
            />
            <Button
              variant="contained"
              onClick={handleSearch}
              startIcon={<SearchIcon />}
            >
              Поиск
            </Button>
          </Box>
        </Paper>
      </Box>

      {/* Сетка фильмов */}
      <Grid container spacing={3}>
        {filteredFilms.map((item) => (
          <Grid item xs={12} sm={6} md={4} lg={3} key={item.id}>
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
              <CardMedia
                component="img"
                height="250"
                image={item.cinema.urlimage || 'https://via.placeholder.com/300x200?text=No+Image'}
                alt={item.cinema.headname}
                sx={{ objectFit: 'cover' }}
              />
              <CardContent sx={{ flexGrow: 1 }}>
                <Typography variant="h6" component="h3" gutterBottom>
                  {item.cinema.headname}
                </Typography>
                
                <Typography variant="body2" color="text.secondary" paragraph>
                  {item.cinema.director} • {item.cinema.year}
                </Typography>
                
                <Chip
                  label={item.cinema.zhanr}
                  size="small"
                  sx={{ mb: 1 }}
                />
                
                <Box sx={{ mb: 2 }}>
                  <Typography variant="body2" color="text.secondary">Статус:</Typography>
                  <Chip
                    label={item.statuscinema}
                    size="small"
                    color={
                      item.statuscinema === 'Просмотрено' ? 'success' :
                      item.statuscinema === 'Смотрю сейчас' ? 'primary' :
                      item.statuscinema === 'Хочу посмотреть' ? 'warning' : 'default'
                    }
                  />
                </Box>
                
                <Box sx={{ mb: 2 }}>
                  <Typography variant="body2" color="text.secondary">Ваша оценка:</Typography>
                  <Rating value={item.ratinguser} readOnly size="small" />
                  {item.ratinguser > 0 && (
                    <Typography variant="body2" sx={{ ml: 1, display: 'inline' }}>
                      ({item.ratinguser})
                    </Typography>
                  )}
                </Box>
                
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 'auto' }}>
                  <Button
                    variant="outlined"
                    startIcon={<EditIcon />}
                    onClick={() => handleEditClick(item)}
                  >
                    Изменить
                  </Button>
                  <IconButton
                    color="error"
                    onClick={() => handleDelete(item)}
                  >
                    <DeleteIcon />
                  </IconButton>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {filteredFilms.length === 0 && (
        <Box sx={{ textAlign: 'center', mt: 10 }}>
          <MovieIcon sx={{ fontSize: 60, color: 'text.secondary', mb: 2 }} />
          <Typography variant="h6" color="text.secondary">
            {myFilms.length === 0 
              ? 'Ваша коллекция пуста. Добавьте фильмы из общего каталога!'
              : 'Фильмы не найдены'}
          </Typography>
        </Box>
      )}

      {/* Диалог редактирования */}
      <Dialog open={editDialogOpen} onClose={() => setEditDialogOpen(false)}>
        <DialogTitle>Редактирование фильма</DialogTitle>
        <DialogContent>
          {selectedFilm && (
            <>
              <Typography variant="h6" gutterBottom>
                {selectedFilm.cinema.headname}
              </Typography>
              
              <FormControl fullWidth sx={{ mt: 2, mb: 2 }}>
                <InputLabel>Статус просмотра</InputLabel>
                <Select
                  value={editForm.statuscinema}
                  label="Статус просмотра"
                  onChange={(e) => setEditForm({ ...editForm, statuscinema: e.target.value })}
                >
                  {statusOptions.map((status) => (
                    <MenuItem key={status} value={status}>{status}</MenuItem>
                  ))}
                </Select>
              </FormControl>
              
              <Box sx={{ mt: 2 }}>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  Ваша оценка:
                </Typography>
                <Rating
                  value={editForm.ratinguser}
                  onChange={(e, newValue) => setEditForm({ ...editForm, ratinguser: newValue })}
                  precision={1}
                  size="large"
                />
                {editForm.ratinguser > 0 && (
                  <Typography variant="body2" sx={{ ml: 2, display: 'inline' }}>
                    ({editForm.ratinguser}/10)
                  </Typography>
                )}
              </Box>
            </>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setEditDialogOpen(false)}>Отмена</Button>
          <Button onClick={handleEditSubmit} variant="contained">Сохранить</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default MyFilms;
