import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container, Typography, Box, Grid, Card, CardContent,
  CardMedia, Button, Rating, Alert
} from '@mui/material';
import MovieIcon from '@mui/icons-material/Movie';
import PeopleIcon from '@mui/icons-material/People';
import CollectionsIcon from '@mui/icons-material/Collections';

const HomePage = () => {
  const [userRole, setUserRole] = useState('NULL');
  const [films, setFilms] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    checkUser();
    fetchFilms();
  }, []);

  const checkUser = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/current-user');
      const role = await response.text();
      setUserRole(role);
    } catch (error) {
      console.error('Ошибка:', error);
    }
  };

  const fetchFilms = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/films');
      const data = await response.json();
      // Берем только 4 фильма для главной страницы
      setFilms(data.slice(0, 4));
    } catch (error) {
      console.error('Ошибка загрузки фильмов:', error);
    }
  };

  const features = [
    {
      icon: <MovieIcon sx={{ fontSize: 60 }} color="primary" />,
      title: 'Коллекция фильмов',
      description: 'Создайте свою уникальную коллекцию фильмов, оценивайте и комментируйте'
    },
    {
      icon: <PeopleIcon sx={{ fontSize: 60 }} color="secondary" />,
      title: 'Сообщество',
      description: 'Находите друзей, делитесь своими коллекциями и открывайте новые фильмы'
    },
    {
      icon: <CollectionsIcon sx={{ fontSize: 60 }} color="success" />,
      title: 'Управление',
      description: 'Легко управляйте своей коллекцией, ставьте статусы просмотра и рейтинги'
    }
  ];

  return (
    <Container maxWidth="lg">
      <Box sx={{ textAlign: 'center', my: 8 }}>
        <Typography variant="h2" component="h1" gutterBottom sx={{ fontWeight: 'bold' }}>
          Добро пожаловать в FilmBase
        </Typography>
        <Typography variant="h5" color="text.secondary" paragraph>
          Ваша персональная коллекция фильмов
        </Typography>
        
        {userRole === 'NULL' && (
          <Box sx={{ mt: 4 }}>
            <Alert severity="info" sx={{ mb: 2 }}>
              Для доступа ко всем функциям войдите в систему или зарегистрируйтесь
            </Alert>
            <Button
              variant="contained"
              size="large"
              onClick={() => navigate('/login')}
              sx={{ mr: 2 }}
            >
              Войти
            </Button>
            <Button
              variant="outlined"
              size="large"
              onClick={() => navigate('/register')}
            >
              Зарегистрироваться
            </Button>
          </Box>
        )}
      </Box>

      {/* Особенности */}
      <Box sx={{ my: 8 }}>
        <Typography variant="h4" component="h2" gutterBottom align="center">
          Возможности платформы
        </Typography>
        <Grid container spacing={4} sx={{ mt: 2 }}>
          {features.map((feature, index) => (
            <Grid item xs={12} md={4} key={index}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', p: 3 }}>
                {feature.icon}
                <CardContent sx={{ textAlign: 'center' }}>
                  <Typography variant="h5" component="h3" gutterBottom>
                    {feature.title}
                  </Typography>
                  <Typography color="text.secondary">
                    {feature.description}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>

      {/* Популярные фильмы */}
      <Box sx={{ my: 8 }}>
        <Typography variant="h4" component="h2" gutterBottom>
          Популярные фильмы
        </Typography>
        <Grid container spacing={3}>
          {films.map((film) => (
            <Grid item xs={12} sm={6} md={3} key={film.id}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <CardMedia
                  component="img"
                  height="200"
                  image={film.urlimage || 'https://via.placeholder.com/300x200?text=No+Image'}
                  alt={film.headname}
                />
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography gutterBottom variant="h6" component="h3">
                    {film.headname}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" paragraph>
                    {film.director} • {film.year}
                  </Typography>
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
                    <Rating value={film.rating} precision={0.5} readOnly size="small" />
                    <Typography variant="body2" color="text.secondary" sx={{ ml: 1 }}>
                      ({film.marks})
                    </Typography>
                  </Box>
                  <Button
                    size="small"
                    onClick={() => navigate('/films')}
                    fullWidth
                  >
                    Подробнее
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Container>
  );
};

export default HomePage;
